import React, { Component } from 'react'
//import Identicon from 'identicon.js';
//const Identicon = require('identicon')
import Account from "./App.js";

class Navbar extends Component {
  async show(){
    const web3 = window.web3  
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    console.log(this.account)

  }
  

  render() {
    return (
      <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
        <a
          className="navbar-brand col-sm-3 col-md-2 mr-0"
          href="Chris_Lytrokapis"
          target="_blank"
          rel="noopener noreferrer"
        >
          Swap Tokens Like a TITAN
        </a>

        <ul className="navbar-nav px-3">
          <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
          <small className="primary-text">
              <small id="account">Logged in account: {this.state.account}</small>
            </small>

            <small className="web3-address">
              <small id="account">{this.props.account}</small>
            </small>

            { this.props.account
              ? <img
                className="ml-2"
                width='30'
                height='30'
                //src={`data:image/png;base64,${new Account(this.props.account, 30).toString()}`}
                src={this.props.account}
                alt=""
              />
              : <span></span>
            }

          </li>
        </ul>
      </nav>
    );
  }
}

export default Navbar;
